// src/App.js
import React from "react";

function App() {
  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 via-white to-purple-100 flex items-center justify-center px-4">
      <div className="bg-white p-8 rounded-2xl shadow-xl w-full max-w-3xl">
        <h1 className="text-3xl font-bold text-center text-purple-700 mb-6">
          GCE Advanced Level Students Directory
        </h1>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          <div className="bg-purple-100 rounded-xl p-4 text-center shadow-md hover:shadow-lg transition-all">
            <h2 className="font-semibold text-lg text-purple-800">Student Name</h2>
            <p className="text-sm text-gray-600">Subject Combination</p>
          </div>
          <div className="bg-blue-100 rounded-xl p-4 text-center shadow-md hover:shadow-lg transition-all">
            <h2 className="font-semibold text-lg text-blue-800">Student Name</h2>
            <p className="text-sm text-gray-600">Subject Combination</p>
          </div>
          <div className="bg-green-100 rounded-xl p-4 text-center shadow-md hover:shadow-lg transition-all">
            <h2 className="font-semibold text-lg text-green-800">Student Name</h2>
            <p className="text-sm text-gray-600">Subject Combination</p>
          </div>
          {/* Add more student cards here */}
        </div>
      </div>
    </div>
  );
}

export default App;
